import React, { useState } from 'react';
import chatLogo from '../style/images/mibachat.png';
import { isEmail } from 'validator';
import Form from 'react-validation/build/form';
import Input from 'react-validation/build/input';
import CheckButton from 'react-validation/build/button';

function Register() {
  const [email, setEmail] = useState('');
  const [nickname, setNickname] = useState('');
  const [password, setPassword] = useState('');

  function handleEmailChange(e) {
    setEmail(e.target.value);
  }
  function handleNicknameChange(e) {
    setNickname(e.target.value);
  }
  function handlePasswordChange(e) {
    setPassword(e.target.value);
  }

  return (
    <div className="signup-container">
      <img src={chatLogo} alt="chatLogo" className="mibachatlogo" />
      <h2>Sign up</h2>
      <Form className="signup-form">
        <div>
          <div className="form-group">
            <label htmlFor="nickname">Nickname</label>
            <Input
              type="text"
              className="form-control"
              name="nickname"
              maxLength="16"
              onChange={handleNicknameChange}
              validations={[required, vefifyUsername]}
            />
          </div>

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <Input
              type="text"
              className="form-control"
              name="email"
              onChange={handleEmailChange}
              validations={[required, verifyEmail]}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <Input
              type="password"
              className="form-control"
              maxLength="16"
              name="password"
              onChange={handlePasswordChange}
              validations={[required, verifyPassword]}
            />
          </div>

          <div className="form-group">
            <button className="submitButton">Sign Up</button>
          </div>
        </div>
      </Form>
    </div>
  );
}

const required = (value) => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        <p>This field is required!</p>
      </div>
    );
  }
};

const verifyEmail = (value) => {
  if (!isEmail(value)) {
    return (
      <div className="alert alert-danger" role="alert">
        <p>{value} is not a valid email.</p>
      </div>
    );
  }
};

const vefifyUsername = (value, props) => {
  if (value.length < 3 || value.length > props.maxLength) {
    return (
      <div className="alert alert-danger" role="alert">
        <p>The username must be between 3 and {props.maxLength} characters.</p>
      </div>
    );
  }
};

const verifyPassword = (value, props) => {
  if (value.length < 6 || value.length > props.maxLength) {
    return (
      <div className="alert alert-danger" role="alert">
        <p>The password must be between 6 and {props.maxLength} characters.</p>
      </div>
    );
  }
};

export default Register;
