import React from 'react';

function Field(props) {
  let type = 'text';
  if (props.fieldName === 'Password') type = 'password';
  return (
    <div class="field">
      <label htmlFor={props.fieldName}>{props.fieldName}â€‰*</label>
      <input type={type} id={props.fieldName} onChange={props.onChange}></input>
      {addWhitespace()}
    </div>
  );
}

function addWhitespace() {
  return (
    <div>
      <br></br>
    </div>
  );
}

export default Field;
