import React, { useState } from 'react';
import Field from './Field';
import FormButton from './FormButton';
import chatLogo from './style/images/mibachat.png';

function Form() {
  const [username, setScriptName] = useState('');
  const [nickname, setErrorCodes] = useState('');
  const [password, setNumAcc] = useState('');

  function handleScriptNameChange(e) {
    setScriptName(e.target.value);
  }
  function handleErrorCodesChange(e) {
    setErrorCodes(e.target.value);
  }
  function handleNumOfAccChange(e) {
    setNumAcc(e.target.value);
  }

  return (
    <div className="formDiv">
      <img src={chatLogo} alt="chatLogo" className="mibachatlogo" />
      <form className="myForm">
        <div>
          <Field fieldName="Username" onChange={handleScriptNameChange} />
          <Field fieldName="Nickname" onChange={handleErrorCodesChange} />
          <Field fieldName="Password" onChange={handleNumOfAccChange} />
          {addWhitespace()}
          <FormButton
            username={username}
            nickname={nickname}
            password={password}
          />
        </div>
      </form>
    </div>
  );

  function addWhitespace() {
    return (
      <div>
        <br></br>
      </div>
    );
  }
}

export default Form;
