import React, { useState } from 'react';
import Field from './Field';
import FormButton from './FormButton';

function Form() {
  const [scriptName, setScriptName] = useState('');
  const [errorCodes, setErrorCodes] = useState('');
  const [numAcc, setNumAcc] = useState('');

  function handleScriptNameChange(e) {
    setScriptName(e.target.value);
  }
  function handleErrorCodesChange(e) {
    setErrorCodes(e.target.value);
  }
  function handleNumOfAccChange(e) {
    setNumAcc(e.target.value);
  }

  return (
    <div class="formDiv">
      <form className="myForm">
        <h2>Login</h2>
        <div>
          <Field fieldName="scriptName" onChange={handleScriptNameChange} />
          <Field fieldName="errorCodes" onChange={handleErrorCodesChange} />
          <Field fieldName="numAcc" onChange={handleNumOfAccChange} />
          {addWhitespace()}
          <FormButton script={scriptName} error={errorCodes} numAcc={numAcc} />
        </div>
      </form>
    </div>
  );

  function addWhitespace() {
    return (
      <div>
        <br></br>
      </div>
    );
  }
}

export default Form;
