import React from 'react';

function Field(props) {
  let type = setInputType(props);
  return (
    <div className="field">
      <label htmlFor={props.fieldName}>{props.fieldName}â€‰*</label>
      <input
        type={type}
        id={props.fieldName}
        onChange={props.onChange}
        placeholder={props.fieldName}
      ></input>
      {addWhitespace()}
    </div>
  );
}

function setInputType(props) {
  let type = 'text';
  if (props.fieldName === 'Password') type = 'password';
  return type;
}

function addWhitespace() {
  return (
    <div>
      <br></br>
    </div>
  );
}

export default Field;
